#! /usr/bin/env node

const fs = require("fs");
const packageFile = fs.readFileSync(`${process.cwd()}/package.json`);
const package = JSON.parse(packageFile);
const { scripts } = package;
const { log } = console;
const chalk = require("chalk");
const rp = require("rightpad");

const primary = "FFFDF3";
const secondary = "0E09B2";

const key = chalk.hex(primary).bgHex(secondary);
const value = chalk.hex(secondary).bgHex(primary);

for (let script in scripts) {
  log(key(rp(script, 30)) + " : " + value(scripts[script]));
}
